import React, { Component } from "react";
import { Table, Skeleton } from "antd";
import { parseAllUserData } from "../javascript/api";
import { Popconfirm } from "antd";
import Switch from "react-switch";

class DeskTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tableData: null
    };
  }

  updateUsers = () => {
    if (this.props.userData != null && this.props.userData[0] != null) {
      this.setState({
        tableData: this.updateUserArray(this.props.userData)
      });
    }
  };

  componentDidUpdate = lastProp => {
    // console.log(lastProp, this.props, this.state);
    if (lastProp !== this.props) this.updateUsers();
  };

  componentDidMount = prop => {
    this.updateUsers();
  };

  updateUserArray = users => {
    return parseAllUserData(users);
  };

  render() {
    const { loading } = this.props;

    if (loading) {
      return (
        <div style={{ padding: "30px" }}>
          <Skeleton active />
          <Skeleton active />
          <Skeleton active />
          <Skeleton active />
          <Skeleton active />
        </div>
      );
    }

    const columns = [
      {
        title: "Name",
        dataIndex: "name",
        key: "name",
        ellipsis: true,
        width: 180,
        sorter: (a, b) => a.name.localeCompare(b.name),
        fixed: "left"
      },
      {
        title: "ID",
        dataIndex: "id",
        key: "id",
        ellipsis: true,
        width: 330,
        sorter: (a, b) => a.id.localeCompare(b.id)
      },
      {
        title: "Activate",
        dataIndex: "raw",
        key: "raw",
        width: 120,
        render: obj => (
          <a>
            <Popconfirm
                  placement="bottomRight"
                  title="Would you like to activate the desk?"
                  onConfirm={this.confirmLogout}
                  okText="Activate"
                  cancelText="Cancel"
            >
              Activate
            </Popconfirm>
          </a>

        ),
        fixed: ""
      },
    ];

    return (
      <Table
        columns={columns}
        pagination={{ showSizeChanger: true }}
        dataSource={this.state.tableData}
        rowKey="id"
      />
    );
  }
}

export default DeskTable;
