import React from "react";
import UserCard from "./UserCard";
import UserTable from "./UserTable";
import ObservationDrawer from "./ObservationDrawer";
import { Layout, Pagination, Row, Col, Result } from "antd";
import { GlobalContext } from "../components/GlobalContext";

const { Content } = Layout;

class UsersListDisplay extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      patientsDummy: [null, null, null, null, null, null, null, null, null],
      page: props.page ? props.page : 0,
      itemPerPage: 9,
      showDrawer: false,
      currentSelectedPatient: null
    };
  }

  static contextType = GlobalContext;

  viewPatientDrawer = patient => {
    this.setState({
      showDrawer: true,
      currentSelectedPatient: patient
    });
  };

  render() {
    let users = this.props.patients ? this.props.patients : this.state.patientsDummy;

    let layout;

    if (this.context.viewInCard) {
      let startIdx = this.state.page * this.state.itemPerPage;

      let pagination = (
        <Pagination
          style={{ textAlign: "center" }}
          disabled={this.props.loading}
          defaultCurrent={1}
          defaultPageSize={this.state.itemPerPage}
          current={this.state.page + 1}
          total={users && users.length > 1 ? users.length : 0}
          onChange={page => {
            this.setState({
              page: page - 1
            });
          }}
        />
      );

      let keyCounter = 0;

      // -------------------- card view
      let cardListItems = patients
        .slice(startIdx, startIdx + this.state.itemPerPage)
        .map(patient => (
          <Col xs={23} sm={23} md={12} lg={8} style={{ padding: "10px" }} key={keyCounter++}>
            <UserCard
              patientData={patient && patient.resource}
              loading={this.props.loading}
              viewPatient={() => {
                this.viewPatientDrawer(patient);
              }}
            ></UserCard>
          </Col>
        ));

      const cardLayout = (
        <div>
          <Content
            style={{
              margin: "0px 16px"
            }}
          >
            <Row>{cardListItems}</Row>
          </Content>
          {pagination}
        </div>
      );
      layout = cardLayout;
    } else {
      // ---------------------------- Table view
      const tableLayout = (
        <UserTable
          loading={this.props.loading}
          patientData={patients}
          viewPatient={patient => {
            console.log(patient);
            this.viewPatientDrawer(patient);
          }}
        ></UserTable>
      );
      layout = tableLayout;
    }
    return (
      <div>
        {patients.length > 0 ? (
          <div>
            {layout}

            <ObservationDrawer
              patient={this.state.currentSelectedPatient}
              visible={this.state.showDrawer}
              onClose={() => {
                this.setState({
                  currentSelectedPatient: null,
                  showDrawer: false
                });
              }}
            ></ObservationDrawer>
          </div>
        ) : (
          <Result title="No search result to display" subTitle="You can try a different keyword" />
        )}
      </div>
    );
  }
}

export default UsersListDisplay;
