import React from "react";
import "./login.css"
import Header from "../components/Header";
import { Redirect } from "react-router-dom";

import login from "../javascript/api";

class LoginPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          islogged: false,
          loginParams: {
            user_id: "",
            user_password: ""
          }
        };
      }

      handleFormChange = event => {
        let loginParamsNew = { ...this.state.loginParams };
        let val = event.target.value;
        loginParamsNew[event.target.name] = val;
        this.setState({
          loginParams: loginParamsNew
        });
      };

      login = event => {
        let id = this.state.loginParams.user_id;
        let password = this.state.loginParams.user_password;
        if (id === "portaluser" && password === "5555") {
          localStorage.setItem("token", "T");
          this.setState({
            islogged: true
          });
        }
        event.preventDefault();
      };

    render() {
        if (localStorage.getItem("token")) {
            return <Redirect to="/" />;
          }
        return (
            <form onSubmit={this.login}>
            <div>
                <Header title="Login"></Header>
                <div className="base-container">
                    <div className="content">
                        <div className="form">
                            <div className="form-group">
                                <label htmlFor="id">ID</label>
                                <input type="text" name="id" onChange={this.handleFormChange}/>
                            </div>
                            <div className="form-group">
                                <label htmlFor="password">Password</label>
                                <input type="password" name="password" onChange={this.handleFormChange}/>
                            </div>
                        </div>
                    </div>
                    <div className="footer">
                        <button type="submit" className="btn">Login</button>
                    </div>
                </div>
            </div>
            </form>
        );
    }
}

export default LoginPage;
