import React from "react";
import "./home.css";
import icon_user from "../img/icon_user.png";
import icon_search from "../img/icon_search.png";
import icon_stats from "../img/icon_stats.png";
import Header from "../components/Header";

import "antd/dist/antd.css";
import LoginPage from "./routes/login";
import UserPage from "./routes/users";
import PortalUserPage from "./routes/portalUsers";
import SearchPage from "./routes/search";
import StatisticsPage from "./routes/statistics";
import DesksPage from "./routes/desks";
import NotFoundPage from "./routes/NotFoundPage";
import SideMenu from "./components/SideMenu";
import MobileTabBar from "./components/MobileTabBar";

import getUsers from "./javascript/api";


import logo from "./img/dashboard-logo.png";
//import icon from "./img/dashboard-logo.png";
import profile from "./img/icon_user.png";

import { CaretDownOutlined, UnorderedListOutlined, AppstoreOutlined } from "@ant-design/icons";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import { BrowserRouter as Router, Switch, Route, Link, useLocation } from "react-router-dom";
import { Layout, Avatar, Popconfirm, Radio, Tooltip, Modal, message } from "antd";
import GlobalContextConsumer from "./components/GlobalContext";
import { GlobalContext, GlobalContextProvider } from "./components/GlobalContext";

import { withRouter } from "react-router-dom";

class DesktopMenu extends React.Component {
  state = {
    collapsed: false
  };

  updateCollapsed = collapsed => {
    this.setState({
      collapsed: collapsed
    });
  };

  componentDidMount() {
    message.config({
      top: 80
    });
    getUsers();
  }

  render() {
    return (
      <Sider
        collapsible
        onCollapse={this.updateCollapsed}
        breakpoint="lg"
        width="230"
        style={{
          boxShadow: "7px 0px 20px -10px rgba(0,0,0,0.35)",
          overflow: "auto",
          height: "100vh",
          position: "sticky",
          top: 0,
          left: 0
        }}
      >
        <Link to="/">
          <div
            className="logo"
            style={{
              background: `url("${this.state.collapsed ? logo : logo}") no-repeat`
            }}
          ></div>
        </Link>
        <SideMenu></SideMenu>
        <div style={{ flexGrow: 1 }}></div>
      </Sider>
    );
  }
}

const routes = [
  {
    path: "/",
    exact: true,
    title: () => "Web Portal",
    main: () => <HomePage />
  },
  {
    path: "/users",
    title: () => "Users",
    main: () => <UserPage />
  },
  {
    path: "/portalUsers",
    title: () => "Portal Users",
    main: () => <PortalUserPage />
  },
  {
    path: "/search",
    title: () => "Search",
    main: () => <SearchPage />
  },
  {
    path: "/statistics",
    title: () => "Statistics",
    main: () => <StatisticsPage />
  },
  {
    path: "/desks",
    title: () => "Desks",
    main: () => <DesksPage />
  },
  {
    path: "/home",
    title: () => "Home",
    main: () => <HomePage />
  },
  {
    path: "/login",
    title: () => "Login",
    main: () => <LoginPage />
  },
  {
    title: () => "404 Not Found",
    main: () => <NotFoundPage />
  }
];

class HomePage extends React.Component {
  render() {
    const { history } = this.props;
    return (
      <Layout style={{ minHeight: 100 + "vh" }}>
          <GlobalContextConsumer>
            {value => {
              if (!value.isMobile) {
                return <DesktopMenu></DesktopMenu>;
              } else {
                return <MobileTabBar></MobileTabBar>;
              }
            }}
          </GlobalContextConsumer>

          <Layout className="site-layout">
            <Header className="site-layout-header">
              <h2 style={{ paddingLeft: 20 + "px" }}>
                <Switch>
                  {routes.map((route, index) => (
                    <Route
                      key={index}
                      path={route.path}
                      exact={route.exact}
                      children={<route.title />}
                    />
                  ))}
                </Switch>
              </h2>

              <div style={{ flexGrow: 1, textAlign: "right", paddingRight: "20px" }}>
                <GlobalContextProvider>
                  <Radio.Group
                    defaultValue="table"
                    onChange={e => {
                      console.log(e);
                      this.handleViewChange(e.target.value);
                    }}
                    size="small"
                  >
                    <Radio.Button value="table">
                      <Tooltip placement="bottom" title="View users in a table">
                        <UnorderedListOutlined />
                      </Tooltip>
                    </Radio.Button>
                    <Radio.Button value="card">
                      <Tooltip placement="bottom" title="View users in cards">
                        <AppstoreOutlined />
                      </Tooltip>
                    </Radio.Button>
                  </Radio.Group>
                </GlobalContextProvider>
                <Popconfirm
                  placement="bottomRight"
                  title="Would you like to logout of the system?"
                  onConfirm={this.confirmLogout}
                  okText="Logout"
                  cancelText="Cancel"
                >
                  <Avatar
                    src={profile}
                    style={{ marginLeft: "20px", marginRight: !this.context.isMobile && "5px" }}
                  />
                  {!this.context.isMobile && <CaretDownOutlined />}
                </Popconfirm>
              </div>
            </Header>

              <div>
              <Header title="Web Portal" isHome="true"></Header>
              <div className="center_float">
                <div
                  className="menu_card"
                  onClick={() => {
                    history.push("/users");
                  }}
                >
                  <img src={icon_user} alt="user" />
                  <div className="text">Users</div>
                </div>
                <div
                  className="menu_card"
                  onClick={() => {
                    history.push("/search");
                  }}
                >
                  <img src={icon_search} alt="user" />
                  <div className="text">Search</div>
                </div>
                <div
                  className="menu_card"
                  onClick={() => {
                    history.push("/statistics");
                  }}
                >
                  <img src={icon_stats} alt="user" />
                  <div className="text">Statistics</div>
                </div>
              </div>
          </div>
          </Layout>
        </Layout>
    );
  }
}

export default withRouter(HomePage);
