    const {password} = req.body;
    const nationID = parseInt(req.body.nationID);

    if (!nationID || !password) {
        errorResp(res, new Error('nationID and Password are required'));
        return;
    }
    

    UserModel.where({nationID:nationID, is_deleted:false})
        .fetch()
        .then((user) => {
            
